	(503, '==Further reading==\\n;General\\n* {{ cite book | author=[[Carey McWilliams (journalist)|Carey McWilliams]] | publisher=Peregrine Smith | title = Southern California: An Island on the Land | edition = 9th | year = 2009 | isbn = 9780879050078}}\\n* {{ cite book | author=[[Richard White (historian)|Richard White]] | title = [[It\'s Your Misfortune and None of My Own]]: A New History of the American West | publisher=University of Oklahoma Press | year = 1991 | isbn = 9780806125671}}\\n* {{ cite book | author =[[David Rieff]] | title = Los Angeles: Capital of the Third World | publisher = Touchstone | year = 1992 | isbn = 9780671792107}}\\n* {{ cite book | author=[[Peter Theroux]] | title = Translating LA: A Tour of the Rainbow City | publisher=Norton | year = 1994 | isbn = 9780393313949}}\\n* {{ cite book | author=[[Paul Glover]] | title = Los Angeles: A History of the Future | publisher=Greenplanners | year = 1995 | isbn = 9780962291104}}\\n* {{ cite book | author=Leonard Pitt & Dale Pitt | title = Los Angeles A to Z: An Encyclopedia of the City and County | publisher=University of California Press | place = Berkeley | year = 2000 | isbn = 9780520205307}}\\n* {{ cite book | author = [[Kevin Starr]] and David Ulin | editor = Jim Heimann | title = Los Angeles: Portrait of a City | publisher = [[Taschen]] America | year = 2009 | isbn = 9783836502917}}'),
	(504, ';Architecture and urban theory\\n* {{ cite book | author=[[Reyner Banham]] | title = Los Angeles: The Architecture of Four Ecologies | publisher=[[University of California Press]] | place = Berkeley | year = 2009 | edition = 2nd | isbn = 9780520260153}}\\n* {{ cite book | author=[[Mike Davis (scholar)|Mike Davis]] | title = [[City of Quartz|City of Quartz: Excavating the Future in Los Angeles]] | publisher=Verso | year = 2006 | isbn = 9781844675685}}\\n* {{ cite book | author=Robert M. Fogelson | title = The Fragmented Metropolis: Los Angeles 1850\\u20131930 | publisher=University of California Press | place = Berkeley | year = 1993 | isbn = 9780520082304}}\\n* {{ cite book | author=Norman M. Klein | title = The History of Forgetting: Los Angeles and the Erasure of Memory | publisher=Verso | year = 1997 | isbn = 9781844672424}}\\n* {{ cite book | author = Sam Hall Kaplan | title = L.A. Lost & Found: An Architectural History of Los Angeles | publisher = Hennessey and Ingalls | year = 2000 | isbn = 9780940512238}}\\n* {{ cite journal | journal=City & Society | volume = XIV | issue = 2 | year = 2002 | author=Torin Monahan | pages = 155\\u2013184 | title =  Los Angeles Studies: The Emergence of a Specialty Field }} [http:\\/\\/torinmonahan.com\\/papers\\/LA_Studies.pdf (available online)]'),
